import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { searchAPI, postsAPI } from '../services/api';
import { Post } from '../types';

const Search: React.FC = () => {
  const [query, setQuery] = useState('');
  const [workEnvironment, setWorkEnvironment] = useState('');
  const [results, setResults] = useState<Post[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [hasSearched, setHasSearched] = useState(false);
  const location = useLocation();

  const handleSearch = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const workEnvArray = workEnvironment
        .split(',')
        .map(env => env.trim())
        .filter(env => env);

      const response = await searchAPI.search({
        query,
        workEnvironment: workEnvArray
      });

      setResults(response.posts || []);
    } catch (err: any) {
      setError(err.response?.data?.message || 'Search failed');
    } finally {
      setLoading(false);
    }
  };

  const handlePostClick = async (postId: number) => {
    try {
      await postsAPI.trackClick(postId);
      // You could open the post in a new tab or modal here
      console.log(`Clicked on post ${postId}`);
    } catch (error) {
      console.error('Failed to track click:', error);
    }
  };

  return (
    <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-4">
          Find APIs & Modules
        </h1>

        <form onSubmit={handleSearch} className="bg-white p-6 rounded-lg shadow">
          <div className="mb-4">
            <label htmlFor="query" className="block text-sm font-medium text-gray-700 mb-2">
              What are you looking for?
            </label>
            <input
              type="text"
              id="query"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-blue-500 focus:border-blue-500"
              placeholder="e.g., REST API library for authentication"
              required
            />
          </div>

          <div className="mb-4">
            <label htmlFor="workEnvironment" className="block text-sm font-medium text-gray-700 mb-2">
              Work Environment (optional)
            </label>
            <input
              type="text"
              id="workEnvironment"
              value={workEnvironment}
              onChange={(e) => setWorkEnvironment(e.target.value)}
              className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-blue-500 focus:border-blue-500"
              placeholder="e.g., React, Node.js, MongoDB (comma-separated)"
            />
          </div>

          <button
            type="submit"
            disabled={loading}
            className="bg-blue-600 text-white px-6 py-2 rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50"
          >
            {loading ? 'Searching...' : 'Search'}
          </button>
        </form>

        {error && (
          <div className="mt-4 bg-red-50 border border-red-200 text-red-600 px-4 py-3 rounded">
            {error}
          </div>
        )}
      </div>

      {results.length > 0 && (
        <div className="space-y-6">
          <h2 className="text-xl font-semibold text-gray-900">
            Search Results ({results.length})
          </h2>

          {results.map((post) => (
            <div
              key={post.id}
              className="bg-white p-6 rounded-lg shadow hover:shadow-md transition-shadow cursor-pointer"
              onClick={() => handlePostClick(post.id)}
            >
              <div className="flex justify-between items-start mb-2">
                <h3 className="text-lg font-medium text-gray-900">
                  {post.title}
                </h3>
                <span className="text-sm text-gray-500">
                  {post.clickCount} clicks
                </span>
              </div>

              {post.summary && (
                <p className="text-gray-600 mb-3">
                  {post.summary}
                </p>
              )}

              <div className="flex flex-wrap gap-2 mb-3">
                {post.apisModules && post.apisModules.map((api, index) => (
                  <span
                    key={index}
                    className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800"
                  >
                    {api}
                  </span>
                ))}
              </div>

              {post.workEnvironment && post.workEnvironment.length > 0 && (
                <div className="flex flex-wrap gap-2 mb-3">
                  {post.workEnvironment.map((env, index) => (
                    <span
                      key={index}
                      className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800"
                    >
                      {env}
                    </span>
                  ))}
                </div>
              )}

              {post.tags && post.tags.length > 0 && (
                <div className="flex flex-wrap gap-2">
                  {post.tags.map((tag, index) => (
                    <span
                      key={index}
                      className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-gray-100 text-gray-800"
                    >
                      #{tag}
                    </span>
                  ))}
                </div>
              )}

              <div className="mt-3 text-sm text-gray-500">
                by {(post as any).username} â€¢ {new Date(post.createdAt).toLocaleDateString()}
              </div>
            </div>
          ))}
        </div>
      )}

      {results.length === 0 && query && !loading && (
        <div className="text-center py-12">
          <p className="text-gray-500">No results found for your search.</p>
        </div>
      )}
    </div>
  );
};

export default Search;
